import React, { useState, useEffect, useContext } from 'react';
import api from '../api/api';
import { AuthContext } from '../context/AuthContext';
import './Orders.css'; // Vamos criar este arquivo para estilização

const Orders = () => {
    // Seus pedidos que já foram feitos
    const [pastOrders, setPastOrders] = useState([]);
    // Todos os produtos disponíveis na loja para adicionar a um novo pedido
    const [allProducts, setAllProducts] = useState([]);
    // Os itens do novo pedido que está sendo montado (o "carrinho")
    const [currentOrderItems, setCurrentOrderItems] = useState([]);
    
    const { auth } = useContext(AuthContext);

    // States de controle
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Função para carregar os dados da página (pedidos antigos e produtos)
    const fetchData = async () => {
        if (!auth.user?.id) return;
        
        setLoading(true);
        try {
            const [ordersResponse, productsResponse] = await Promise.all([
                api.get(`/orders/user/${auth.user.id}`).catch(err => {
                    // Se o usuário não tiver pedidos, a API retorna 404. Tratamos isso como um array vazio.
                    if (err.response && err.response.status === 404) return { data: [] };
                    throw err; // Se for outro erro, ele será pego pelo catch principal
                }),
                api.get('/products')
            ]);

            setPastOrders(ordersResponse.data);
            setAllProducts(productsResponse.data);
            setError('');
        } catch (err) {
            setError("Falha ao carregar os dados da página.");
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    // Roda o fetchData quando o componente carrega ou o usuário muda
    useEffect(() => {
        fetchData();
    }, [auth.user]);

    // Adiciona um produto ao carrinho (novo pedido)
    const handleAddToCart = (product) => {
        if (!product) return;

        setCurrentOrderItems(prevItems => {
            const itemExists = prevItems.find(item => item.id === product.id);
            if (itemExists) {
                // Se o item já existe, apenas aumenta a quantidade
                return prevItems.map(item =>
                    item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
                );
            }
            // Se não existe, adiciona com quantidade 1
            return [...prevItems, { ...product, quantity: 1 }];
        });
    };

    // Remove um item do carrinho
    const handleRemoveFromCart = (productId) => {
        setCurrentOrderItems(prevItems => prevItems.filter(item => item.id !== productId));
    };

    // Altera a quantidade de um item no carrinho
    const handleQuantityChange = (productId, newQuantity) => {
        const quantity = parseInt(newQuantity, 10);
        if (quantity < 1) {
            handleRemoveFromCart(productId);
            return;
        }
        setCurrentOrderItems(prevItems =>
            prevItems.map(item =>
                item.id === productId ? { ...item, quantity: quantity } : item
            )
        );
    };

    // Finaliza e envia o pedido para o backend
    const handlePlaceOrder = async () => {
        if (currentOrderItems.length === 0) {
            alert("Seu carrinho está vazio!");
            return;
        }

        const orderPayload = {
            userId: auth.user.id,
            products: currentOrderItems.map(item => ({
                productId: item.id,
                quantity: item.quantity,
            })),
        };

        try {
            await api.post('/orders', orderPayload);
            alert("Pedido realizado com sucesso!");
            setCurrentOrderItems([]); // Limpa o carrinho
            fetchData(); // Atualiza a lista de pedidos antigos
        } catch (err) {
            setError("Ocorreu um erro ao finalizar o pedido.");
            console.error(err);
        }
    };

    if (loading) {
        return <p>Carregando seus pedidos...</p>;
    }

    return (
        <div className="orders-page">
            <div className="form-container new-order-section">
                <h3>Criar Novo Pedido</h3>
                <div className="add-product-form">
                    <select onChange={(e) => handleAddToCart(allProducts.find(p => p.id === parseInt(e.target.value)))} defaultValue="">
                        <option value="" disabled>Selecione um produto para adicionar...</option>
                        {allProducts.map(product => (
                            <option key={product.id} value={product.id}>
                                {product.name} - R$ {product.price}
                            </option>
                        ))}
                    </select>
                </div>

                <h4>Itens do Pedido</h4>
                <div className="cart-items">
                    {currentOrderItems.length === 0 ? (
                        <p>Nenhum item no pedido.</p>
                    ) : (
                        currentOrderItems.map(item => (
                            <div key={item.id} className="cart-item">
                                <span>{item.name} (R$ {item.price})</span>
                                <div className='cart-item-actions'>
                                    <input
                                        type="number"
                                        value={item.quantity}
                                        onChange={(e) => handleQuantityChange(item.id, e.target.value)}
                                        min="1"
                                    />
                                    <button onClick={() => handleRemoveFromCart(item.id)} className="remove-btn">Remover</button>
                                </div>
                            </div>
                        ))
                    )}
                </div>
                {currentOrderItems.length > 0 && (
                     <button onClick={handlePlaceOrder} className="place-order-btn">Finalizar Pedido</button>
                )}
            </div>

            <div className="past-orders-section">
                <h2>Meus Pedidos</h2>
                {error && <p className="error">{error}</p>}
                {pastOrders.length === 0 && !loading && <p>Você ainda não fez nenhum pedido.</p>}

                <div className="item-list">
                    {pastOrders.map(order => (
                        <div key={order.id} className="item-card order-card">
                            <h4>Pedido #{order.id}</h4>
                            <ul>
                                {order.products?.map(product => (
                                    <li key={product.id}>
                                        {product.OrderProduct.quantity}x {product.name}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Orders;
