// src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode'; // Importe a biblioteca
import api from '../api/api';

export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({
        token: localStorage.getItem('token'),
        user: null, // O objeto do usuário começará como nulo
        loading: true, // Um estado de loading para a autenticação inicial
    });

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decodedUser = jwtDecode(token);
                setAuth({ token: token, user: decodedUser, loading: false });
                api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            } catch (error) {
                console.error("Token inválido:", error);
                // Se o token for inválido, limpa tudo
                localStorage.removeItem('token');
                setAuth({ token: null, user: null, loading: false });
            }
        } else {
            // Se não houver token, o carregamento terminou
            setAuth(prevState => ({ ...prevState, loading: false }));
        }
    }, []);

    const login = (token) => {
        try {
            const decodedUser = jwtDecode(token);
            localStorage.setItem('token', token);
            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            setAuth({ token: token, user: decodedUser, loading: false });
        } catch (error) {
            console.error("Erro ao decodificar token no login:", error);
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        delete api.defaults.headers.common['Authorization'];
        setAuth({ token: null, user: null, loading: false });
    };

    // O valor do contexto agora inclui o objeto 'auth' completo
    const value = { auth, login, logout };

    return (
        <AuthContext.Provider value={value}>
            {/* Renderiza os filhos apenas quando o loading inicial terminar */}
            {!auth.loading && children}
        </AuthContext.Provider>
    );
};
