import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, AuthContext } from './context/AuthContext'; // Verifique o caminho se necessário

// Importando o CSS aqui garante que ele seja aplicado em todo o app
import './App.css'; 

// Componentes e Páginas
import Navbar from './components/layoutNavbar';
import Login from './pages/Login';
import Register from './pages/Register';
import Categories from './pages/Categories';
import Products from './pages/Products';
import Orders from './pages/Orders';

// Um componente para proteger rotas que precisam de login
const PrivateRoute = ({ children }) => {
  const { auth } = React.useContext(AuthContext);
  return auth.token ? children : <Navigate to="/login" />;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <main>
          <Routes>
            {/* Rotas Públicas */}
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />

            {/* Rotas Privadas */}
            <Route path="/categories" element={<PrivateRoute><Categories /></PrivateRoute>} />
            <Route path="/products" element={<PrivateRoute><Products /></PrivateRoute>} />
            <Route path="/orders" element={<PrivateRoute><Orders /></PrivateRoute>} />

            {/* Rota Padrão */}
            <Route path="/" element={<Navigate to="/categories" />} />
          </Routes>
        </main>
      </Router>
    </AuthProvider>
  );
}

export default App;
