import React, { useState, useEffect, useContext } from 'react';
import api from '../api/api'; // <--- Declaração do 'api'
import { AuthContext } from '../context/AuthContext'; // <--- Declaração do 'AuthContext'

const Orders = () => {
    // Declarações de State (useState) que estavam faltando:
    const [pastOrders, setPastOrders] = useState([]);
    const [allProducts, setAllProducts] = useState([]);
    const [currentOrderItems, setCurrentOrderItems] = useState([]);
    const [pageLoading, setPageLoading] = useState(true);
    const [error, setError] = useState('');
    
    // Declaração do 'auth' e 'loading' vindo do Contexto
    const { auth, loading: authLoading } = useContext(AuthContext);

    const fetchData = async () => {
        if (!auth.user?.id) {
            setPageLoading(false);
            return;
        }
        
        setPageLoading(true);
        try {
            const [ordersResponse, productsResponse] = await Promise.all([
                api.get('/orders/myorders'), // Rota corrigida
                api.get('/products')
            ]);
            
            setPastOrders(ordersResponse.data);
            setAllProducts(productsResponse.data);
        } catch (err) {
            setError("Falha ao carregar os dados da página.");
            console.error("Erro em fetchData:", err);
        } finally {
            setPageLoading(false);
        }
    };

    useEffect(() => {
        if (!authLoading && auth.user) {
            fetchData();
        } else if (!authLoading) {
            setPageLoading(false);
        }
    }, [authLoading, auth.user]);

    const handleAddToCart = (product) => {
        if (!product) return;
        setCurrentOrderItems(prevItems => {
            const itemExists = prevItems.find(item => item.id === product.id);
            if (itemExists) {
                return prevItems.map(item =>
                    item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
                );
            }
            return [...prevItems, { ...product, quantity: 1 }];
        });
    };

    const handleRemoveFromCart = (productId) => {
        setCurrentOrderItems(prevItems => prevItems.filter(item => item.id !== productId));
    };

    const handleQuantityChange = (productId, newQuantity) => {
        const quantity = parseInt(newQuantity, 10);
        if (quantity < 1) {
            handleRemoveFromCart(productId);
            return;
        }
        setCurrentOrderItems(prevItems =>
            prevItems.map(item =>
                item.id === productId ? { ...item, quantity: quantity } : item
            )
        );
    };

    const handlePlaceOrder = async () => {
        if (currentOrderItems.length === 0) {
            alert("Seu carrinho está vazio!");
            return;
        }
        const orderPayload = {
            products: currentOrderItems.map(item => ({
                productId: item.id,
                quantity: item.quantity,
            })),
        };
        try {
            await api.post('/orders', orderPayload);
            alert("Pedido realizado com sucesso!");
            setCurrentOrderItems([]);
            fetchData(); // Recarrega os pedidos
        } catch (err) {
            setError("Ocorreu um erro ao finalizar o pedido.");
            console.error(err);
        }
    };

    if (authLoading || pageLoading) {
        return <p className="page-container">Carregando...</p>;
    }

    return (
        <div className="page-container">
            <h1 className="page-header">Meus Pedidos</h1>
            <div className="orders-page">
                <div className="card">
                    <h3>Criar Novo Pedido</h3>
                    <div className="form-group">
                       <label>Adicionar produto ao carrinho:</label>
                        <select className="form-control" onChange={(e) => {
                            const selectedProduct = allProducts.find(p => p.id === parseInt(e.target.value));
                            handleAddToCart(selectedProduct);
                            e.target.value = ""; // Reseta o select
                        }} defaultValue="">
                            <option value="" disabled>Selecione um produto...</option>
                            {allProducts.map(product => (
                                <option key={product.id} value={product.id}>
                                    {product.name} - R$ {product.price}
                                </option>
                            ))}
                        </select>
                    </div>

                    <h4 style={{marginTop: 'var(--spacing-lg)'}}>Itens do Pedido Atual</h4>
                    {currentOrderItems.length === 0 ? (
                        <p>Nenhum item no pedido.</p>
                    ) : (
                        currentOrderItems.map(item => (
                            <div key={item.id} className="cart-item">
                                <span>{item.name} (R$ {item.price})</span>
                                <div className='cart-item-actions'>
                                    <input
                                        type="number"
                                        className="form-control"
                                        value={item.quantity}
                                        onChange={(e) => handleQuantityChange(item.id, e.target.value)}
                                        min="1"
                                    />
                                    <button onClick={() => handleRemoveFromCart(item.id)} className="btn btn-danger">X</button>
                                </div>
                            </div>
                        ))
                    )}
                    {currentOrderItems.length > 0 && (
                         <button onClick={handlePlaceOrder} className="btn btn-primary" style={{width: '100%', marginTop: 'var(--spacing-md)'}}>Finalizar Pedido</button>
                    )}
                </div>

                <div className="card">
                    <h2>Histórico de Pedidos</h2>
                    {error && <p className="error">{error}</p>}
                    {pastOrders.length === 0 ? (
                        <p>Você ainda não fez nenhum pedido.</p>
                    ) : (
                        pastOrders.map(order => (
                            <div key={order.id} className="list-group-item" style={{flexDirection: 'column', alignItems: 'flex-start'}}>
                                <h4>Pedido #{order.id}</h4>
                                <ul style={{listStyle: 'none', padding: 0, width: '100%'}}>
                                    {order.products?.map(product => (
                                        <li key={product.id} style={{display: 'flex', justifyContent: 'space-between'}}>
                                            <span>{product.OrderProduct.quantity}x {product.name}</span>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
};

export default Orders;
