import React, { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // 1. Importar o Link
import { AuthContext } from '../context/AuthContext';
import api from '../api/api';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/users/login', { email, password });
      login(response.data.token);
      navigate('/categories'); // Redireciona para a página principal após o login
    } catch (error) {
      // O interceptor do Axios já mostra o erro na tela.
      console.error('Falha no login', error);
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <h2>Login</h2>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Digite seu email"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Senha</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Digite sua senha"
            required
          />
        </div>
        <button type="submit" className="button-primary">Entrar</button>
      </form>
      <div className="redirect-link">
        <p>
          Não tem uma conta? <Link to="/register">Cadastre-se aqui</Link>
        </p>
      </div>
      
    </div>
  );
};

export default Login;
