import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Importação das páginas e componentes
import Login from './pages/Login';
import Register from './pages/Register';
import Categories from './pages/Categories';
import Products from './pages/Products';
import Orders from './pages/Orders';
import ProtectedRoute from './components/layout/ProtectedRoute';
import Navbar from './components/layout/Navbar';

// Um componente de Layout para páginas internas
const MainLayout = ({ children }) => (
  <>
    <Navbar />
    <main className="page-container">{children}</main>
  </>
);

function App() {
  return (
    <AuthProvider>
      <ToastContainer position="top-right" autoClose={4000} />
      <Router>
        <Routes>
          {/* Rota Raiz: agora é a página de Login */}
          <Route path="/" element={<Login />} />
          
          {/* Rota de Registro (se você tiver uma) */}
          <Route path="/register" element={<Register />} />

          {/* Rotas Protegidas: agora usam um layout com Navbar */}
          {/* A "home" da aplicação pode ser a página de categorias, por exemplo */}
          <Route path="/categories" element={<ProtectedRoute><MainLayout><Categories /></MainLayout></ProtectedRoute>} />
          <Route path="/products" element={<ProtectedRoute><MainLayout><Products /></MainLayout></ProtectedRoute>} />
          <Route path="/orders" element={<ProtectedRoute><MainLayout><Orders /></MainLayout></ProtectedRoute>} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
