import React, { useState, useEffect } from 'react';
import api from '../api/api'; // Usando nossa instância do Axios

const Products = () => {
    // States para produtos
    const [products, setProducts] = useState([]);
    const [editingProduct, setEditingProduct] = useState(null);

    // States para o formulário
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [categoryId, setCategoryId] = useState('');

    // State para carregar e exibir as categorias no dropdown
    const [categories, setCategories] = useState([]);

    // States de controle
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Função para buscar os dados iniciais (produtos e categorias)
    const fetchData = async () => {
        setLoading(true);
        try {
            // Busca as duas listas em paralelo para ser mais rápido
            const [productsResponse, categoriesResponse] = await Promise.all([
                api.get('/products'),
                api.get('/categories')
            ]);
            
            setProducts(productsResponse.data);
            setCategories(categoriesResponse.data);
            setError('');

        } catch (err) {
            console.error("Erro ao buscar dados:", err);
            setError("Não foi possível carregar os dados. Tente novamente.");
        } finally {
            setLoading(false);
        }
    };

    // Roda o fetchData() uma vez quando o componente é montado
    useEffect(() => {
        fetchData();
    }, []);

    const resetForm = () => {
        setName('');
        setPrice('');
        setCategoryId('');
        setEditingProduct(null);
        setError('');
    };

    const handleEdit = (product) => {
        setEditingProduct(product);
        setName(product.name);
        setPrice(product.price);
        setCategoryId(product.categoryId);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Tem certeza que deseja deletar este produto?')) {
            try {
                await api.delete(`/products/${id}`);
                fetchData(); // Recarrega os dados após deletar
            } catch (err) {
                setError("Erro ao deletar o produto.");
                console.error(err);
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!name || !price || !categoryId) {
            setError("Todos os campos são obrigatórios.");
            return;
        }

        const productData = { name, price: parseFloat(price), categoryId: parseInt(categoryId) };

        try {
            if (editingProduct) {
                await api.put(`/products/${editingProduct.id}`, productData);
            } else {
                await api.post('/products', productData);
            }
            resetForm();
            fetchData(); // Recarrega os dados após criar/editar
        } catch (err) {
            setError("Ocorreu um erro ao salvar o produto.");
            console.error(err);
        }
    };

    if (loading) {
        return <p>Carregando...</p>;
    }

    return (
        <div>
            <h2>Gerenciar Produtos</h2>

            <div className="form-container">
                <h3>{editingProduct ? 'Editar Produto' : 'Criar Novo(a) Produto'}</h3>
                <form onSubmit={handleSubmit}>
                    <input type="text" placeholder="Produto" value={name} onChange={(e) => setName(e.target.value)} required />
                    <input type="number" placeholder="Preço" value={price} onChange={(e) => setPrice(e.target.value)} required step="0.01" />
                    
                    {/* AQUI ESTÁ A MUDANÇA PRINCIPAL */}
                    <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
                        <option value="" disabled>Selecione uma categoria</option>
                        {categories.map((cat) => (
                            <option key={cat.id} value={cat.id}>
                                {cat.name}
                            </option>
                        ))}
                    </select>

                    <button type="submit">{editingProduct ? 'Atualizar' : 'Adicionar'}</button>
                    {editingProduct && <button type="button" onClick={resetForm}>Cancelar</button>}
                    {error && <p className="error">{error}</p>}
                </form>
            </div>

            <div className="item-list">
                {products.map(product => (
                    <div key={product.id} className="item-card">
                        <h4>{product.name}</h4>
                        <p>Preço: R$ {product.price}</p>
                        <p>ID da Categoria: {product.categoryId}</p>
                        <div>
                            <button onClick={() => handleEdit(product)}>Editar</button>
                            <button onClick={() => handleDelete(product.id)}>Deletar</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Products;
